import React, { Component } from 'react';

const estados = ['Acre', 'Alagoas', 'Amapá', 'Amazonas', 'Bahia', 'Ceará', 'Distrito Federal', 'Espírito Santo', 'Goiás', 'Maranhão', 'Mato Grosso', 'Mato Grosso do Sul', 'Minas Gerais', 'Pará', 'Paraíba', 'Paraná', 'Pernambuco', 'Piauí', 'Rio de Janeiro', 'Rio Grande do Norte', 'Rio Grande do Sul', 'Rondônia', 'Roraima', 'Santa Catarina', 'São Paulo', 'Sergipe', 'Tocantins'];

class PersonalForm extends Component{
  render() {
    return (
      <fieldset className="personalData">
        <legend>Dados pessoais</legend>

          Nome:
          <input type="text"  name="name" maxLength="40" required/>

          Email:
          <input type="text"  name="email" maxLength="50" required/>

          CPF:
          <input type="text"  name="cpf" maxLength="11" required/>

          Endereço:
          <input type="text"  name="address" maxLength="200" required/>

          Cidade:
          <input type="text"  name="city" maxLength="28" required/>

          Estado:
          <select>
            <option>Selecione</option>
            {estados.map((estado, index) => <option key={index}>{estado}</option>)}
          </select>

      </fieldset>
    )
  }
}

export default PersonalForm;
